import React, { useEffect, useState } from 'react'
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import { useSelector } from 'react-redux';

const DropdownCell = (props) => {
  const periods = useSelector(state => state.periods.periods)
  const [dropdownOptions, setDropdownOptions] = useState([])
  const [isLoaded, setIsLoaded] = useState(false)
  const [selectedPeriod, setSelectedPeriod] = useState({ 
    value: props.type == 'period-dropdown' ? props.data:props.cost_increase_period_id
  , label: periods.find(each => (props.type == 'period-dropdown' ? props.data:props.cost_increase_period_id) == each._id) })

  useEffect(() => {
    if (!isLoaded) {
      const shallowPeriodOptions = periods.map(each => {
        return { value: each._id, label: each.name.th }
      })
      setDropdownOptions(shallowPeriodOptions)
      setIsLoaded(true)
    }
  }, [dropdownOptions])

  return (
    <div style={{ width: `${props.width}px` }}>
      {props.type == 'period-dropdown' &&
        <Dropdown
          onSelect={(valueKey) => props.onCellChange(props.type, props.address.tableId, props.address.rowId, props.colIndex, valueKey)}
          style={{
            width: `${props.width}px`,
          }}
        >
          <Dropdown.Toggle id="dropdown-autoclose-true">
            {selectedPeriod.label.name.th}
          </Dropdown.Toggle>
          <Dropdown.Menu>
            {dropdownOptions && dropdownOptions.map((option) => (
              <Dropdown.Item
                eventKey={option.value}
                style={{
                  width: `${props.width}px`,
                }}
              >
                {option.label}
              </Dropdown.Item>
            ))}
          </Dropdown.Menu>
        </Dropdown>
      }
      {props.type == 'cost-increase-dropdown' &&
        <div className='d-flex'>
          <input 

          />
          <Dropdown
            onSelect={(valueKey) => props.onCellChange(props.type, props.address.tableId, props.address.rowId, props.colIndex, valueKey)}
            style={{
              width: `${props.width}px`,
            }}
          >
            <Dropdown.Toggle id="dropdown-autoclose-true">
              {selectedPeriod.label.name.th}
            </Dropdown.Toggle>
            <Dropdown.Menu>
              {dropdownOptions && dropdownOptions.map((option) => (
                <Dropdown.Item
                  eventKey={option.value}
                  style={{
                    width: `${props.width}px`,
                  }}
                >
                  {option.label}
                </Dropdown.Item>
              ))}
            </Dropdown.Menu>
          </Dropdown>
        </div>
      }
      {props.type == 'asset-account-dropdown' &&
        <div>
          asset-account
        </div>
      }
    </div>
  )
}

export default DropdownCell
