/* eslint-disable react-hooks/rules-of-hooks */
/* eslint-disable no-undef */
import React, { useState } from "react";
import "./infoProject.css";
import BizTextInfo from "../bizTextInfo/bizTextInfo";
import makeAnimated from 'react-select/animated';

import BizLogo from "../bizLogo/bizLogo";

import "./infoProject.css";
import Select from 'react-select'

import { updateProject } from "../../../features/projectsSlice";

function infoProject(props) {
  const animatedComponents = makeAnimated();

  const { register, handleSubmit } = useForm();
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const industries = useSelector(state => state.industries.industries)
  const currencies = useSelector(state => state.currencies.currencies)
  const selectedProject = useSelector(state => state.projects.selectedProject)
  const [industryOptions, setIndustryOptions] = useState([])
  const [currencyOptions, setCurrencyOptions] = useState([])
  const [selectedCurrencyId, setSelectedCurrencyId] = useState()
  const [selectedIndustryIds, setSelectedIndustryIds] = useState([])
  const [isOptionLoaded,setIsOptionLoaded] = useState(false)

  const [file, setFile] = useState()
  const [imageUrl, setImageUrl] = useState("")

  useEffect(() => {
    if (!isOptionLoaded) {
      const shallowIndustryOptions = industries.map((each) => {
        return { value: each._id, label: each.name.th }
      })
      const shallowCurrencyOptions = currencies.map((each) => {
        return { value: each._id, label: each.name.local }
      })
      setIndustryOptions(shallowIndustryOptions)
      setCurrencyOptions(shallowCurrencyOptions)
      setIsOptionLoaded(true)
    }
    const projectShallow = {
      user_id: selectedProject.user_id,
      name: selectedProject.name,
      industry_ids: selectedProject.industry_ids,
      description: selectedProject.description,
      logo_url: selectedProject.logo_url,
      created_date: selectedProject.created_date,
      modified_date: new Date(),
      sale_trends: selectedProject.sale_trends,
      business_goals: selectedProject.business_goals,
      model_config: {
        projection_period: Number(selectedProject.projection_period),
        start_date: selectedProject.start_date,
        currency_id: selectedCurrencyId,
        working_hours: Number(selectedProject.working_hours),
        income_tax_rate: Number(selectedProject.income_tax_rate),
        discounting_rate: Number(selectedProject.discounting_rate),
      },
      ...selectedProject
    }
    dispatch(updateProject(projectShallow))
  }, [selectedProject])

  // const doSubmit = (data) => {
  //   uploadData(data)
  // }

  // const uploadData = (data) => {
  //   const formData = new FormData();
  //   formData.append("image", file);
  //   axios.post("http://localhost:5000/", formData, {
  //     headers: { "Content-Type": "multipart/form-data" },
  //   }).then(res => {
  //     setImageUrl(res.data.filename)
  //     setEvent(data)
  //   })
  // }

  const onUploadChange = (e) => {
    setFile(e.target.files[0])
  }

  const onCurrencyChange = (e) => {
    dispatch(projectUpdated({
      ...selectedProject,
      model_config: {
        ...selectedProject.model_config,
        currency_id: e.value
      }
    }))
  }

  const onIndustryChange = (e) => {
    const shallowSelectedIndustryIds = e.map((each) => {
      return each.value
    })
    dispatch(projectUpdated({
      ...selectedProject,
      industry_ids: shallowSelectedIndustryIds
    }))
  }

  return (
    <div className="new-invest-form">
      <div className="d-flex label-newInvest-pj">

        <div>
          <div className="input-container">
            <BizTextInfo title="ชื่อธุรกิจ" />
            <input
              className="input-newInvest-pj"
              style={{ width: "280px" }}
              type="name"
              onChange={(e) => {
                dispatch(projectUpdated({
                  ...selectedProject,
                  name: e.target.value
                }))
              }}
              required
            />
          </div>
          <div className="d-flex flex-col">
            <div className="input-container ">
              <div className="label-newInvest-pj">โลโก้ธุรกิจ </div>
              <BizLogo />
              <div>
                <button
                  for="getFiles"
                  type="button"
