import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'

const NumbersTableCell = (props) => {
  const selectedProject = useSelector(state => state.projects.selectedProject)

  useEffect(() => {
  }, [selectedProject, props.handleFunction, props.onCellChange])

  const [periodMonthsName,setPeriodMonthsName] = useState([])

  const monthsName = [
    "มกราคม",
    "กุมภาพันธ์",
    "มีนาคม",
    "เมษายน",
    "พฤษภาคม",
    "มิถุนายน",
    "กรกฏาคม",
    "สิงหาคม",
    "กันยายน",
    "ตุลาคม",
    "พฤศจิกายน",
    "ธันวาคม",
  ]

  const initialPeriodMonths = (startDate) => {
    let date = new Date(startDate)
    let startMonthIndex = date.getMonth()
    let result = []
    for(let i = startMonthIndex;11-startMonthIndex; i++){
      result.push(monthsName[i])
    }
    for(let i = 0;12*(selectedProject.model_config.projection_period-1); i++){
      result.push(monthsName[i])
    }
    for(let i = 0;startMonthIndex; i++){
      result.push(monthsName[i])
    }
    setPeriodMonthsName(result)
  }
  console.log(selectedProject.model_config.start_date);
  return (
    <div className='d-flex numbers-table-style'  >
      {props.data.map((each, i) =>
      <div className="d-flex">
        <div className='seasonal-month-style'>
        {periodMonthsName[i]}
        </div>
        {/* <input className='seasonal-cell-style'
          value={each}
          onKeyPress={(e) => !/[0-9\b]+/.test(e.key) && e.preventDefault()}
          onChange={e =>
            props.onCellChange(
              props.tableType,
              props.address.tableId,
              props.address.rowId,
              props.colIndex,
              { index: i, value: e.target.value }
            )
          }
        /> */}
        </div>
      )}
    </div>
  )
}

export default NumbersTableCell
