import React from "react";
import "./projectTempleteCard.css";
import { SiHomeassistantcommunitystore } from "react-icons/si";

function projectTempleteCard(props) {
  const setChildrenToActive = (state, status) => {
    let theIcon = document.getElementById(`this-icon-${props.data._id}`)
    let theTitle = document.getElementById(`this-title-${props.data._id}`)
    if (state === true) {
      theIcon.classList.toggle(status)
      theTitle.classList.toggle(status)

    }
    else {
      theIcon.classList.remove(status)
      theTitle.classList.remove(status)
    }
  }

  return (
    <div  id={`this-card-${props.data._id}`}  className="pjt-card card-group" onMouseDown={() => setChildrenToActive(true, 'active')} onMouseUp={() => setChildrenToActive(false, 'active')}>
      <div>
        <div id={`this-icon-${props.data._id}`} className="mb-1 pjt-icon card-group d-flex justify-content-center align-items-center">
          <SiHomeassistantcommunitystore />
        </div>
        <div id={`this-title-${props.data._id}`} className="pjt-title card-group d-flex justify-content-center align-items-center">{props.data.projectData.name}</div>
      </div>
    </div>
  );
}

export default projectTempleteCard;
