import React, { useEffect, useState } from 'react'
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import { useSelector } from 'react-redux';

const DropdownCell = (props) => {
  const periods = useSelector(state => state.periods.periods)
  const [periodOptions, setPeriodOptions] = useState([])
  const [isLoaded, setIsLoaded] = useState(false)
  const [selectedPeriod, setSelectedPeriod] = useState({ value: props.data, label: periods.find(each => props.data == each._id) })

  useEffect(() => {
    if (!isLoaded) {
      const shallowPeriodOptions = periods.map(each => {
        return { value: each._id, label: each.name.th }
      })
      setPeriodOptions(shallowPeriodOptions)
      setIsLoaded(true)
    }
  }, [periodOptions])

  return (
    <div style={{ width: `${props.width}px` }}>
      {/* {JSON.stringify(selectedPeriod)} */}
      {props.type == 'period-dropdown' &&
        <Dropdown
          onSelect={(valueKey) => props.onCellChange(props.type, props.address.tableId, props.address.rowId, props.colIndex, valueKey)}
          style={{
            width: `${props.width}px`,
          }}
        >
          <Dropdown.Toggle id="dropdown-autoclose-true">
            {selectedPeriod.name.th}
          </Dropdown.Toggle>
          <Dropdown.Menu>
            {periodOptions && periodOptions.map((option) => (
              <Dropdown.Item
                eventKey={option.value}
                style={{
                  width: `${props.width}px`,
                }}
              >
                {option.label}
              </Dropdown.Item>
            ))}
          </Dropdown.Menu>
        </Dropdown>
      }
      {props.type == 'cost-increase-dropdown' &&
        <div>
          cost-increase
        </div>
      }
      {props.type == 'asset-account-dropdown' &&
        <div>
          asset-account
        </div>
      }
      {props.type == 'period-dropdown' &&
        <div>
          period
        </div>
      }
    </div>
  )
}

export default DropdownCell
