export default function KCalculator(allData, myInvest) {
    const debts = allData.debt_issuance_tables[0].debt_issuances //array
    let totalDebts = 0
    debts.forEach((eachDebt) => {
        totalDebts = totalDebts+eachDebt.amount
    })
    const invests = allData.equity_contribution_tables[0].equity_contributions //array
    let totalInvests = 0
    invests.forEach((eachInvest) => {
        totalInvests = totalInvests+eachInvest.amount
    })
    const totalAmount = totalDebts+totalInvests
    const share = (myInvest*100)/totalAmount
    return share
}
