import React, { useEffect } from 'react'
import { useSelector } from 'react-redux'

const NumbersTableCell = (props) => {
  const selectedProject = useSelector(state => state.projects.selectedProject)

  useEffect(() => {
  }, [selectedProject, props.handleFunction, props.onCellChange])

  const monthsName = [
    "มกราคม",
    "กุมภาพันธ์",
    "มีนาคม",
    "เมษายน",
    "พฤษภาคม",
    "มิถุนายน",
    "กรกฏาคม",
    "สิงหาคม",
    "กันยายน",
    "ตุลาคม",
    "พฤศจิกายน",
    "ธันวาคม",
  ]

  const indeOfMonths = (startDate, projectionPeriod) => {
    let date = new Date(startDate)
    console.log(date);
    return date.getMonth()
  }
  console.log(selectedProject.model_config.start_date);
  return (
    <div className='d-flex numbers-table-style'  >
      {indeOfMonths(selectedProject.model_config.start_date,selectedProject.model_config.projection_period)}
      {/* {props.data.map((each, i) =>
      <div className="d-flex">
        <div className='seasonal-month-style'>
        {monthsName[i]}
        </div>
        <input className='seasonal-cell-style'
          value={each}
          onKeyPress={(e) => !/[0-9\b]+/.test(e.key) && e.preventDefault()}
          onChange={e =>
            props.onCellChange(
              props.tableType,
              props.address.tableId,
              props.address.rowId,
              props.colIndex,
              { index: i, value: e.target.value }
            )
          }
        />
        </div>
      )} */}
    </div>
  )
}

export default NumbersTableCell
