import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'

const NumbersTableCell = (props) => {
  const selectedProject = useSelector(state => state.projects.selectedProject)

  const [isLoaded, setIsLoaded] = useState(false)
  useEffect(() => {
    if(!isLoaded){
      initialPeriodMonths(selectedProject.model_config.start_date)
      setIsLoaded(true)
    }
  }, [selectedProject, props.handleFunction, props.onCellChange])

  const [periodMonthsName, setPeriodMonthsName] = useState([])

  const monthsName = [
    "มกราคม",
    "กุมภาพันธ์",
    "มีนาคม",
    "เมษายน",
    "พฤษภาคม",
    "มิถุนายน",
    "กรกฏาคม",
    "สิงหาคม",
    "กันยายน",
    "ตุลาคม",
    "พฤศจิกายน",
    "ธันวาคม",
  ]

  const initialPeriodMonths = (startDate) => {
    let date = new Date(startDate)
    let startMonthIndex = date.getMonth()
    let result = []
    for (let i = startMonthIndex; 11 - startMonthIndex; i++) {
      result.push({ year: 1, month: monthsName[i] })
    }
    for (let i = 0; 12 * (selectedProject.model_config.projection_period - 1); i++) {
      result.push({ year: 2 + i / 12, month: monthsName[i] })
    }
    for (let i = 0; startMonthIndex; i++) {
      result.push({ year: selectedProject.model_config.projection_period, month: monthsName[i] })
    }
    setPeriodMonthsName(result)
  }

  return (
    <div className='d-flex numbers-table-style'  >
      {periodMonthsName.map((each, i) =>
        <div className="d-flex">
          <div className='seasonal-month-style'>
            <div>
              <div>
                ปีที่ {periodMonthsName[i].year}
              </div>
              <div>
                เดือน {periodMonthsName[i].month}
              </div>
            </div>
          </div>
          <input className='seasonal-cell-style'
            // value={each}
            onKeyPress={(e) => !/[0-9\b]+/.test(e.key) && e.preventDefault()}
          // onChange={e =>
          //   props.onCellChange(
          //     props.tableType,
          //     props.address.tableId,
          //     props.address.rowId,
          //     props.colIndex,
          //     { index: i, value: e.target.value }
          //   )
          // }
          />
        </div>
      )}
    </div>
  )
}

export default NumbersTableCell
