import React from "react";
import "./projectTempleteCard.css";
import { SiHomeassistantcommunitystore } from "react-icons/si";

function projectTempleteCard(props) {
  const setChildrenToActive = (state) => {
    let theIcon = document.getElementById('this-icon')
    let theTitle = document.getElementById('this-title')
    if (state === true) {
      console.log('true')
      theIcon.classList.toggle("active")
      theTitle.classList.toggle("active")
    }
    else {
      console.log('false')
      theIcon.classList.remove("active")
      theTitle.classList.remove("active")
    }
  }

  return (
    <div className="pjt-card" onMouseDown={() => setChildrenToActive(true)} onMouseUp={() => setChildrenToActive(false)}>
      <div>
        <div id='this-icon' className="mb-1 pjt-icon">
          <SiHomeassistantcommunitystore />
        </div>
        <div id='this-title' className="pjt-title">{props.data.projectData.name}</div>
      </div>
    </div>
  );
}

export default projectTempleteCard;
