import React from 'react'

function timeToShow (props){
    const year = props.date[0,3]
    const month = props.date[5,6]
    const day = props.date[8,9]
    if (props.type == "input-date") {
        return `${day}/${month}/${year}`
    }
}

export default timeToShow
