import React from "react";
import "./projectTempleteCard.css";
import { SiHomeassistantcommunitystore } from "react-icons/si";

function projectTempleteCard(props) {
  const setChildrenToActive = (state, status) => {
    let theIcon = document.getElementById('this-icon')
    let theTitle = document.getElementById('this-title')
    if (state === true) {
      if(state === 'active'){
      theIcon.classList.toggle(status)
      theTitle.classList.toggle(status)
      }
      else{
        console.log('focus');
        theIcon.focus()
        theTitle.focus()
      }
    }
    else {
      if(state === 'active'){
      theIcon.classList.remove(status)
      theTitle.classList.remove(status)
      }
      else{
        console.log('blur focus');
        document.activeElement.blur()
      }
    }
  }

  return (
    <div className="pjt-card card-group" onMouseDown={() => setChildrenToActive(true, 'active')} onMouseUp={() => setChildrenToActive(false, 'active')}
               onMouseEnter ={() => setChildrenToActive(true, 'focus')} onMouseLeave={() => setChildrenToActive(false, 'focus')}>
      <div>
        <div id='this-icon' className="mb-1 pjt-icon card-group d-flex justify-content-center align-items-center">
          <SiHomeassistantcommunitystore />
        </div>
        <div id='this-title' className="pjt-title card-group d-flex justify-content-center align-items-center">{props.data.projectData.name}</div>
      </div>
    </div>
  );
}

export default projectTempleteCard;
