import React from 'react'
import KCalulateFunctions from '../../../../../common/kCalulator'
import InputCell from './inputCell'

const PaymentsTable = (props) => {
  return (
    <div className='mt-4'>
      <div className='d-flex mb-2'>
        <div>จำนวนยอดหนี้คงเหลือ:</div>
        <div className='fw-bold text-danger'>&nbsp;{KCalulateFunctions.getRemainDebt(props.data)}</div>
      </div>
      <div>
        <div className='d-flex'>
          <div className='bg-dark text-light'>รายการที่กู้ยืม</div>
        </div>
        {JSON.stringify(props.data)}
        {props.data.map((eachRow, index) => 
          <div className='d-flex'>
            <InputCell
              disabled={true}
              data={eachRow.name}
              type="text"
              width={200} />
          </div>
        )}
      </div>
    </div>
  )
}

export default PaymentsTable
