import React from "react";
import "./biztoolConfirmPopup.css";
import onClickOutside from "react-onclickoutside";
import BizTextInfo from "../bizTools/bizTextInfo/bizTextInfo";
import { AiOutlineClose } from "react-icons/ai";

function BiztoolConfirmPopup(props) {

  return props.trigger ? (
    <div className="confirm-popup-overlay">
      <div className="confirm-popup-card">

        {props.leftTitle &&
          <div className="d-flex flex-col align-items-center justify-content-between">

            <BizTextInfo title={props.leftTitle} />
            <AiOutlineClose onClick={props.close} />

          </div>}
        <hr></hr>

        {props.preTitle &&
          <div className=" flex-col justify-content-center  d-flex">
            <div className="preTitle-header-style">{props.preTitle}</div>
            <div className="justify-content-end">
              <AiOutlineClose onClick={props.close} />
            </div>
          </div>}
        {props.title &&
          <div className=" d-flex justify-content-center align-items-center text-align-center confirm-title-header-style ">
            {props.title}
          </div>}
        <div className="align-items-center confirm-popup-content">
          {props.content}
        </div>
        <div className="d-flex justify-content-between mx-2 nb-2">
            {props.leftButton}
            {props.rightButton}
        </div>
      </div>
    </div>
  ) : null;
}

export default onClickOutside(BiztoolConfirmPopup);
