import React, { useEffect, useState } from 'react'
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import { useSelector } from 'react-redux';

const DropdownCell = (props) => {
  const periods = useSelector(state => state.periods.periods)
  const [periodOptions,setPeriodOptions] = useState([])

  useEffect(() => {
    const shallowPeriodOptions = periods.map(each => { 
      return {value: each._id, label: each.name.th}
    })
    setPeriodOptions(shallowPeriodOptions)

  }, [periodOptions])

  return (
    <div style={{width: `${props.width}px`}}>
      {props.type == 'period-dropdown' &&
       <DropdownButton id="dropdown-basic-button"
       title={eachCell.val}
       onSelect={(valueKey) => onChangeHandle(eachRow.rowId, eachCell.colId, valueKey)}
       style={{
         width: `${columnStyles[cellIndex].width}px`,
       }}
     >
       {props.tableStyle.column[cellIndex].enumData.map((option) => (
         <Dropdown.Item eventKey={option.value}
           style={{
             width: `${columnStyles[cellIndex].width}px`,
           }}
         >
           {option.title}
         </Dropdown.Item>
       ))}
     </DropdownButton>
      }
      {props.type == 'cost-increase-dropdown' &&
        <div>
          cost-increase
        </div>
      }
      {props.type == 'asset-account-dropdown' &&
        <div>
          asset-account
        </div>
      }
      {props.type == 'period-dropdown' &&
        <div>
          period
        </div>
      }
    </div>
  )
}

export default DropdownCell
