import React, { useState } from 'react'
import './createNewProject.css'

const typeOfMainCategory = {
  hairAndBeauty: {
    type: "HAIR & BEAUTY",
    text: "ผมและความงาม",
  },
  healthCare: {
    type: "HEALTH CARE",
    text: "สุขภาพ",
  },
}
const CreateProjectTemplate = (props) => {
  const [searchInput, setSearchInput] = useState('')
  const [selectedMainIndustry, setSelectedMainIndustry] = useState('Poppular')

  const onSearchChange = (val) => {
    setSearchInput(val)
  }
  const onMainCategoryChange = (type) => {
    // if (type === typeOfMainCategory.hairAndBeauty.type) {
    //   alert('01')
    // }
    // else if (type === typeOfMainCategory.healthCare.type) {
    //   alert('02')
    // }
    setSelectedMainIndustry(type)
  }

  return (
    <div >
      <div className='d-flex'>
        {/* Main Category */}
        <div className='main-category-industry ' style={{ width: `200px` }}>
          <div className='main-category-industry-header fw-bold mb-2'>หมวดหมู่ธุรกิจ</div>
          <button className='main-category-industry-text' onClick={() => onMainCategoryChange(typeOfMainCategory.hairAndBeauty.type)}>
            {typeOfMainCategory.hairAndBeauty.text}
          </button>
          <button className='main-category-industry-text' onClick={() => onMainCategoryChange(typeOfMainCategory.healthCare.type)}>
            {typeOfMainCategory.healthCare.text}
          </button>
          <div>
          </div>
        </div>

        <div>
          {/* Search */}
          <div className=' d-flex align-items-center px-2 ' style={{ height: '60px' }}>
            <input
              style={{ width: '250px', height: '40px', marginRight: '20px' }}
              placeholder='ค้นหาตัวอย่างเทมเพลตธุรกิจที่นี่'
              className='p-2'
              type='text'
              value={searchInput}
              onChange={e => onSearchChange(e.target.value)}
            />
            <div className='d-flex justify-content-center align-items-center'>
              <button className='create-project-template-search-button'>ค้นหา </button>
            </div>
          </div>
          {/* Template list */}
          <div className='template-list border border-secondary' style={{ width: `380px`, minHeight: '410px' }}>
            {selectedMainIndustry === 'Poppular' ? <div id={selectedMainIndustry} className='industry-template-list'>
              <div>เทมเพลตนอดนิยม</div>
              <div>
                data
              </div>
            </div> :
              selectedMainIndustry === typeOfMainCategory.hairAndBeauty.type ?
                <div id={typeOfMainCategory.hairAndBeauty.type} className='industry-template-list'>
                  Hair
                </div> :
                <div id={typeOfMainCategory.healthCare.type} className='industry-template-list'>
                  Health
                </div>
            }
          </div>
        </div>
      </div>
    </div>
  )
}

export default CreateProjectTemplate
