import React, { useState } from 'react'
import './createNewProject.css'
import { useSelector } from 'react-redux'
import AttributeExplaination from './attributeExplanation'
import CPTPage from './cptPage'

const CreateNewProject = (props) => {
  const shallowCreateProject = useSelector(state => state.projects.shallowCreateProject)
  const [currentStep, setCurrentStep] = useState(shallowCreateProject.name === 'เทมเพลตเปล่า' ? 0 : 1)
  const stepHeader = [
    "ประเภทธุรกิจของคุณ",
    "ข้อมูลพื้นฐานเกี่ยวกับธุรกิจของคุณ",
    "วันเริ่มต้นธุรกิจและระยะเวลาในการประเมินธุรกิจ",
    "สกุลเงิน, อัตราภาษีและอัตราเงินคิดลด",
    "อัตรายอดขายในแต่ละปี",
    "รูปภาพโลโกของธุรกิจ",
  ]
  const onStepChange = (destinationNumber) => {
    if (destinationNumber >= 0) setCurrentStep(destinationNumber)
  }

  const createProject = (data) => {
    console.log('create porject');
    console.log(data);
  }
  return (
    <div className='border border-primary m-3 mt-5'>
      <div className='border border-secondary fw-bold'>สร้างโปรเจกธุรกิจใหม่</div>
      <div className='fw-bold'>{`ขั้นตอนที่ ${currentStep}/5 :`}</div>
      <div className='step-header-style'>{stepHeader[currentStep]}</div>
      <div className=''>
        <div className='d-flex'>
          {currentStep === 0 ?
            <CPTPage data={shallowCreateProject} inputs={["industry"]}
              description={<AttributeExplaination industry={true} />} /> :
            currentStep === 1 ?
              <CPTPage data={shallowCreateProject} inputs={["name", "description"]}
                description={<AttributeExplaination nameDescription={true} />} /> :
              currentStep === 2 ?
                <CPTPage data={shallowCreateProject} inputs={["start-date", "projection-period"]}
                  description={<AttributeExplaination startDateProjectionPeriod={true} />} /> :
                currentStep === 3 ?
                  <CPTPage data={shallowCreateProject} inputs={["currency", "tax-rate", "discount-rate"]}
                    description={<AttributeExplaination currencyTaxDiscount={true} />} /> :
                  currentStep === 4 ?
                    <CPTPage data={shallowCreateProject} inputs={["sale-trend",]}
                      description={<AttributeExplaination saleTrend={true} />} /> :
                    <CPTPage data={shallowCreateProject} inputs={["picture",]}
                      description={<AttributeExplaination picture={true} />} />
          }
        </div>
        <div className="d-flex justify-content-center align-items-center border border-primary" style={{ height: '100px', width: '1000px' }}>
          <button onClick={() => onStepChange(currentStep - 1)}>
            ย้อนกลับ
          </button>
          {currentStep <= 4 ?
            <button onClick={() => onStepChange(currentStep + 1)}>
              ถัดไป
            </button> :
            <button onClick={() => createProject(currentStep + 1)}>
              สร้างโปรเจก
            </button>
          }
        </div>
      </div>
    </div>
  )
}

export default CreateNewProject
