import React from "react";
import "./projectTempleteCard.css";
import { SiHomeassistantcommunitystore } from "react-icons/si";

function projectTempleteCard(props) {
  const setChildrenToActive = (state, status) => {
    let theIcon = document.getElementById('this-icon')
    let theTitle = document.getElementById('this-title')
    if (state === true) {
      theIcon.classList.toggle(status)
      theTitle.classList.toggle(status)
    }
    else {
      theIcon.classList.remove(status)
      theTitle.classList.remove(status)
    }
  }

  return (
    <div className="pjt-card" onMouseDown={() => setChildrenToActive(true, 'active')} onMouseUp={() => setChildrenToActive(false, 'active')}
               onMouseEnter ={() => setChildrenToActive(true, 'hover')} onMouseLeave={() => setChildrenToActive(false, 'hover')}>
      <div>
        <div id='this-icon' className="mb-1 pjt-icon d-flex justify-content-center align-items-center">
          <SiHomeassistantcommunitystore />
        </div>
        <div id='this-title' className="pjt-title  d-flex justify-content-center align-items-center">{props.data.projectData.name}</div>
      </div>
    </div>
  );
}

export default projectTempleteCard;
